import getRandomEmoji from '@sefinek/random-emoji';

type EmojyItem = ReturnType<typeof getRandomEmoji.emojis>;
const MAX_ATTEMPT = 100;

export const randomEmojiCreator = {
  /** in memory state that track every emojy already used */
  emojiCreatedNames: new Set<EmojyItem>(),
  /** Generate a random emoji, that is not already used */
  generateOne: () => {
    let attempt = 0;
    while (true) {
      attempt++;
      const outputEmoji = getRandomEmoji.emojis();
      const isAlreadyUsed = randomEmojiCreator.emojiCreatedNames.has(outputEmoji.name);
      if (
        !isAlreadyUsed
        ||
        attempt >= MAX_ATTEMPT
      ) {
        randomEmojiCreator.emojiCreatedNames.add(outputEmoji.name);
        return outputEmoji;
      }
    }
  }
};
