    });
    expect(calculateFrequenciesStats({ A: 1, B: 2 })).toMatchObject({
      total: 3,
      groups: {
        A: { count: 1, percentageOnTotal: 0.3333333333333333 },
        B: { count: 2, percentageOnTotal: 0.6666666666666666 },
      }
    });
  });

});

